#!/bin/sh
curl 'http://localhost:8080/graphql' -H 'Accept-Encoding: gzip, deflate, br' -H 'Content-Type: application/json' -H 'Accept: application/json' -H 'Connection: keep-alive' -H 'DNT: 1' -H 'Origin: http://localhost:8080' --data-binary '{"query":"\nmutation CreateTransaction($data: CreateTransactionInput!) {\n  transaction(data: $data) {\n    transactionExternalId\n    transactionStatus {\n      name\n    }\n    transactionType {\n      name\n    }\n    value\n  }\n}\n\n","variables":{"data":{"value":1002,"tranferTypeId":1,"accountExternalIdDebit":"some-guid","accountExternalIdCredit":"some-guid"}}}' --compressed